import { useRef, useEffect } from "react";

const CameraComponent: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement>(null);

  useEffect(() => {
    const findLogitechCamera = async (): Promise<MediaDeviceInfo | null> => {
      try {
        const devices = await navigator.mediaDevices.enumerateDevices();
        const logitechCamera = devices.find(
          (device) => device.kind === 'videoinput' && device.label.includes('C920')
        );
        return logitechCamera || null;
      } catch (error) {
        console.error('Error enumerating devices:', error);
        return null;
      }
    };

    const startCamera = async (): Promise<void> => {
      const logitechCamera = await findLogitechCamera();
      if (logitechCamera) {
        const constraints: MediaStreamConstraints = {
          video: { deviceId: logitechCamera.deviceId },
        };

        navigator.mediaDevices.getUserMedia(constraints)
          .then((stream) => {
            if (videoRef.current) {
              videoRef.current.srcObject = stream;
            }
          })
          .catch((error) => {
            console.error('Error accessing camera:', error);
          });
      } else {
        console.error('Logitech camera not found.');
      }
    };

    startCamera();
  }, []);
  const takePicture = () => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;

      // Set the canvas dimensions to match the video stream
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw the current video frame onto the canvas
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Convert the canvas image to a data URL
      const dataUrl = canvas.toDataURL('image/png');

      // Save the image as a file
      saveAs(dataUrl, 'webcam_picture.png');
    }
  };
  return <video ref={videoRef} autoPlay muted />;

  };



export default CameraComponent;
