import React, { createContext, useContext, useState, ReactNode, FC } from 'react';

export const CapturedImageContext = createContext<HTMLImageElement | null>(null);

export const useCapturedImage = () => useContext(CapturedImageContext);

type Props = {
    children: ReactNode;
}
export const CapturedImageProvider: FC<Props> = ({ children }) => {
  const [capturedImage, setCapturedImage] = useState<null | null>(null);

  return (
    <CapturedImageContext.Provider value={capturedImage}>
      {children}
    </CapturedImageContext.Provider>
  );
};
