import { useRef, useEffect, useState, useContext } from "react";
import { CapturedImageContext } from "../contexts/CapturedImageContext";

const CameraComponent: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const capturedImageContext = useContext(CapturedImageContext);

  const [countdown, setCountdown] = useState(5);
  const [isCounting, setIsCounting] = useState(false);

  useEffect(() => {
    let timer: NodeJS.Timeout;

    if (isCounting) {
      timer = setInterval(() => {
        setCountdown((prevCountdown) => prevCountdown - 1);
      }, 1000);
    }

    return () => clearInterval(timer);
  }, [isCounting]);

  useEffect(() => {
    if (countdown === 0 && isCounting) {
      takePicture
      console.log('Function called!');
    }
  }, [countdown, isCounting]);

  const startCountdown = () => {
    setCountdown(5); // Set initial countdown value
    setIsCounting(true); // Start the countdown
  };

  useEffect(() => {
    const findLogitechCamera = async (): Promise<MediaDeviceInfo | null> => {
      try {
        const devices = await navigator.mediaDevices.enumerateDevices();
        const logitechCamera = devices.find(
          (device) =>
            device.kind === "videoinput" && device.label.includes("C920")
        );
        return logitechCamera || null;
      } catch (error) {
        console.error("Error enumerating devices:", error);
        return null;
      }
    };

    const startCamera = async (): Promise<void> => {
      const logitechCamera = await findLogitechCamera();
      if (logitechCamera) {
        const constraints: MediaStreamConstraints = {
          video: { deviceId: logitechCamera.deviceId },
        };

        navigator.mediaDevices
          .getUserMedia(constraints)
          .then((stream) => {
            if (videoRef.current) {
              videoRef.current.srcObject = stream;
            }
          })
          .catch((error) => {
            console.error("Error accessing camera:", error);
          });
      } else {
        console.error("Logitech camera not found.");
      }
    };

    startCamera();
  }, []);

  const takePicture = () => {
    if (videoRef.current && canvasRef.current) {
      const video = videoRef.current;
      const canvas = canvasRef.current;

      // Set the canvas dimensions to match the video stream
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw the current video frame onto the canvas
      const context = canvas.getContext("2d");
      context?.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Convert the canvas image to a data URL
      const dataUrl = canvas.toDataURL("image/png");

      // Update the state with the captured image
      if (capturedImageContext) {
        capturedImageContext.setCapturedImage(dataUrl);
      }
      setCapturedImage(dataUrl);
    }
  };

  return (
    <div>
      <video ref={videoRef} autoPlay muted />
      <canvas ref={canvasRef} style={{ display: "none" }} />
      {countdown > 0 ? (
        <p>Countdown: {countdown}</p>
      ) : (
        <p>{isCounting ? 'Countdown finished!' : 'Click the button to start the countdown.'}</p>
      )}

      {!isCounting && (
        <button onClick={startCountdown} disabled={countdown > 0}>
          Start Countdown
        </button>
      )}
    </div>

  );
};

export default CameraComponent;
