import React, { createContext, useContext, useState, ReactNode, FC } from 'react';
import ICapturedImage from '../interfaces/ICapturedImage';
export const CapturedImageContext = createContext<string | null>(null);

export const useCapturedImage = () => useContext(CapturedImageContext);

type Props = {
    children: ReactNode;
}
const CapturedImageProvider: React.FC = ({ children }) => {
    const [capturedImage, setCapturedImage] = useState<string | null>(null);

    return (
      <CapturedImageContext.Provider value={{ capturedImage, setCapturedImage }}>
        {children}
      </CapturedImageContext.Provider>
    );
  };
