import React, { useContext, useEffect } from 'react';
import axios, { AxiosResponse } from "axios";
import { CapturedImageContext } from '../contexts/CapturedImageContext';

const BackgroundRemoverService = async () => {
  const capturedImage = useContext(CapturedImageContext)
  const axios = require('axios');
  const FormData = require('form-data');
  const fs = require('fs');

  const formData = new FormData();
  formData.append('size', 'auto');
    if(capturedImage){
      formData.append("image_url", capturedImage);
  }

  axios({
    method: 'post',
    url: 'https://api.remove.bg/v1.0/removebg',
    data: formData,
    responseType: 'arraybuffer',
    headers: {
      ...formData.getHeaders(),
      'X-Api-Key': 'dZBaGSbPMhnwXCZeHgtYEXMi',
    },
    encoding: null
    })
    .then((response: { status: number; statusText: any; data: any; }) => {
      if(response.status != 200) return console.error('Error:', response.status, response.statusText);
        fs.writeFileSync("tester.png", capturedImage?.setCapturedImage(response.data));
    })
    .catch((error: any) => {
      return console.error('Request failed:', error);
    });
};

export default BackgroundRemoverService;
